import React from 'react';
import { useState } from "react";
import ReactDOM from "react-dom/client";


function Burger() {
  const [items, setItems] = useState([
    {
      id: 0,
      ingredient: "meat",
      price: 100,
      quantity: 0,
    },
    {
      id: 1,
      ingredient: "cheese",
      price: 50,
      quantity: 0,
    },
  ]);
  function removeIngredient(id) {
    console.log(id)
    setItems( items =>
     items.map((item) => {
      if (item.id === id) {
        return {...item, quantity: item.quantity+1}
      }
      return item
    }))
  }

  return (
    <div className="App">
      <ul>
        {items.map((item) => (
          <div key={item.id}>
            <p>{item.ingredient}</p>
            <p>{item.quantity}</p>
            <p>{item.price}</p>
          </div>
        ))}
      </ul>
      <div className="burgerChoice">
      <button onClick={() => removeIngredient(0)}>Add</button>      </div>
    </div>
  );
}
export default Burger;
