import React from 'react';
import { useState, useEffect } from "react";
import ReactDOM from "react-dom/client";


function Burger()
{
  const [price, setTotalPrice] = useState(0)
  const [items, setItems] = useState([
    {
      id: 0,
      ingredient: "meat",
      price: 100,
      quantity: 0,
    },
    {
      id: 1,
      ingredient: "cheese",
      price: 50,
      quantity: 0,
    },
  ]);

  useEffect(() => {
    const sum = items.reduce((accumulator, object) => {
      return accumulator + object.price;
    }, 0);
    setTotalPrice(sum);
  }, [items]);

  // function renderBurger(){
  //   {items.map((item)=>{
  //     return(
  //   <div key={item.id} className={item.ingredient}>
  //   </div>
  //     );
  //   });
  // }

  function addIngredient(id) {
    let updatedPrice = 0
    let item_quantity = 0
      const updated_items = items.map((item) => {
       if (item.id === id) {
        item_quantity = item.quantity+1
        // updatedPrice = price+(item.price*item_quantity)
        return {...item, quantity: item_quantity}
      }
      return item
    })
    setItems(updated_items)
  }
  // const ingredient_div = document.createElement('div');
  // ingredient_div.className = item.ingredient;
  // document.getElementById('ingredients').appendChild(ingredient_div);
  // console.log(updatedPrice)
  // document.getElementById('total-price').innerHTML = updatedPrice

  function removeIngredient(id) {
    // let updatedPrice = 0
    // let item_quantity = 0
    //   const updated_items = items.map((item) => {
    //      if(item.id === id && item.quantity!==0){
    //       {...item, quantity: item_quantity}
    //      }
    //   })
    }

  return (
    <div className="App">
      <div className='total'>
      <span id="total-price"> {price} </span>
      </div>
      <div className="topBun">
      </div>
      <div id="ingredients"></div>
      <div className="bottomBun">
      </div>
      <ul>
        {items.map((item) => (
          <div key={item.id}>
            <p>{item.ingredient}</p>
            <p>{item.quantity}</p>
            <p>{item.price}</p>
          </div>
        ))}
      </ul>
      <div className="burgerChoice">
      <button onClick={() => addIngredient(0)}>Add Meat</button>
      <button onClick={() => addIngredient(1)}>Add Cheese </button>
      <button onClick={() => removeIngredient(0)}>Remove Meat</button>
      <button onClick={() => removeIngredient(1)}>Remove Cheese </button>
      </div>
      </div>
  );
}
export default Burger;
