import React from 'react';
import { User } from '../App';
import { useState} from "react";
const Signup = () => {
  const [user, setUser] = useState([
    {
    email: "text@test.com",
    password: "text123*"
    }
  ]);

  function onSubmit(fname) {
    console.log("++++++++++++++++++++++++++++++++++++++++");

    if(user.email === fname.email){
      console.log("true");
    }
    else
    {
      console.log("false");
    }
  }

  return (
    <>
      <h1>Please, log in!</h1>

      <label>Email:</label>
      <input
        type="text"
        onChange={(event) => {
          setUser.email(event.target.value);
        }}
      />
      <label>Password:</label>
      <input
        type="password"
        onChange={(event) => {
          setUser.password(event.target.value);
        }}
      />
      <button onClick={() => addIngredient(0)}>Add Meat</button>
      <User.Consumer>
      {(fname)=>{
        onSubmit(fname);
      }}
      </User.Consumer>
    </>
  )
}
export default Signup;
