import React from 'react';
import { useState } from "react";
import ReactDOM from "react-dom/client";


function Burger() {
  const total_price =0
  const [items, setItems] = useState([
    {
      id: 0,
      ingredient: "meat",
      price: 100,
      quantity: 0,
    },
    {
      id: 1,
      ingredient: "cheese",
      price: 50,
      quantity: 0,
    },
  ]);
  function addIngredient(id) {

      const updated_items = items.map((item) => {
       console.log("678767898789")
       if (item.id === id) {
        const ingredient_div = document.createElement('div');
        ingredient_div.className = item.ingredient;
        document.getElementsByClassName('ingredients')[0].appendChild(ingredient_div);

        return {...item, quantity: item.quantity+1}
      }
      return item
    })
    setItems(updated_items)
  }
  return (
    <div className="App">
      <div className="topBun">
      </div>
      <div className="ingredients"></div>
      <div className="bottomBun">
      </div>
      <div className='total'>
      <span></span>
      </div>
      <ul>
        {items.map((item) => (
          <div key={item.id}>
            <p>{item.ingredient}</p>
            <p>{item.quantity}</p>
            <p>{item.price}</p>
          </div>
        ))}
      </ul>
      <div className="burgerChoice">
      <button onClick={() => addIngredient(0)}>Add Meat</button>
      <button onClick={() => addIngredient(1)}>Add Cheese </button>
      </div>
      </div>
  );
}
export default Burger;
