import React from 'react';
import { useState } from "react";
import ReactDOM from "react-dom/client";


function Burger() {
  const [price, setTotalPrice] = useState(0)
  const [items, setItems] = useState([
    {
      id: 0,
      ingredient: "meat",
      price: 100,
      quantity: 0,
    },
    {
      id: 1,
      ingredient: "cheese",
      price: 50,
      quantity: 0,
    },
  ]);

  function addIngredient(id) {
    let updatedPrice = 0
    let new_quantity = 0
      const updated_items = items.map((item) => {
       if (item.id === id) {
        new_quantity = item.quantity
        updatedPrice = price+(item.price*item.quantity)
        const ingredient_div = document.createElement('div');
        ingredient_div.className = item.ingredient;
        document.getElementById('ingredients').appendChild(ingredient_div);
        console.log(updatedPrice)
        document.getElementById('total-price').innerHTML = updatedPrice
        return {...item, quantity: item.quantity+1}
      }
      return item
    })
    setItems(updated_items)
    setTotalPrice(updatedPrice)
  }
  return (
    <div className="App">
      <div className='total'>
      <span id="total-price"> {price} </span>
      </div>
      <div className="topBun">
      </div>
      <div id="ingredients"></div>
      <div className="bottomBun">
      </div>
      <ul>
        {items.map((item) => (
          <div key={item.id}>
            <p>{item.ingredient}</p>
            <p>{item.quantity}</p>
            <p>{item.price}</p>
          </div>
        ))}
      </ul>
      <div className="burgerChoice">
      <button onClick={() => addIngredient(0)}>Add Meat</button>
      <button onClick={() => addIngredient(1)}>Add Cheese </button>
      </div>
      </div>
  );
}
export default Burger;
