import React from 'react';
import { useState } from "react";
import ReactDOM from "react-dom/client";


function Burger() {
  const [items, setItems] = useState([
    {
      id: 0,
      ingredient: "meat",
      price: 100,
      quantity: 0,
    },
    {
      id: 1,
      ingredient: "cheese",
      price: 50,
      quantity: 0,
    },
  ]);
  function decrementCount() {
    this.setState(prevState => ({
      items: prevState.items.map(
      obj => (obj._id === 1234 ? Object.assign(obj, { description: "New Description" }) : obj)
    )
  }));
  }

  return (
    <div className="App">
      <ul>
        {items.map((item) => (
          <div key={item.id}>
            <p>{item.ingredient}</p>
            <p>{item.quantity}</p>
            <p>{item.price}</p>
          </div>
        ))}
      </ul>
      <div className="burgerChoice">
        <button onClick={decrementQuantity}>Add Cheese</button>
        <button onClick={incrementQuantity}>Remove Cheese</button>
      </div>
    </div>
  );
}
export default Burger;
