  const [items, setItems] = useState([
    {
      id: 0,
      ingredient: "meat",
      price: 100,
      quantity: 0,
    },
    {
      id: 1,
      ingredient: "cheese",
      price: 50,
      quantity: 0,
    },
  ]);
  function addIngredient(id) {

      const updated_items = items.map((item) => {
       console.log("678767898789")
       if (item.id === id) {
        const ingredient_div = document.createElement('div');
